async function getData(){return await(await fetch("./app/json/data.json")).json()}async function buildChart(){try{var a=await getData(),t=a.map(a=>a.element),e=a.map(a=>a.count),r=document.getElementById("myChart").getContext("2d"),o=(new Chart(r,{type:"bar",data:{labels:t,datasets:[{label:"Count",data:e,backgroundColor:"rgba(75, 192, 192, 0.2)",borderColor:"rgba(75, 192, 192, 1)",borderWidth:1}]},options:{scales:{y:{beginAtZero:!0},x:{barThickness:50,barPercentage:.5,categoryPercentage:1}}}}),e.reduce((a,t)=>a+t,0));console.log("Total:",o)}catch(a){console.error(a)}}buildChart();